mapper.select:
select a.achievement_seq, a.achievement_name, a.achievement_description, a.achievement_condition, a.achievement_type, a.achievement_level, a.achievement_is_active, a.achievement_created_date, (select count(*) from tb_user_achievement ua where ua.achievement_seq = a.achievement_seq and ua.character_seq = #{characterSeq}) as isGained, f.file_path from tb_achievement a LEFT OUTER JOIN tb_file f ON f.relation_tb = 'tb_achievement' AND f.relation_seq = a.achievement_seq where 1 = 1
and achievement_type=#{achievementType}
and level=#{level}

SELECT count(*) from tb_user_achievement 
WHERE achievement_seq = #{achievementSeq} 
AND character_seq = #{characterSeq}
