mapper.update:
update tb_content
set content_like = content_like + 1
where content_seq = #{contentSeq}

update tb_content
set content_like = content_like - 1
where content_seq = #{contentSeq}

update tb_reply
set reply_like = reply_like + 1
where reply_seq = #{replySeq}

update tb_reply
set reply_like = reply_like - 1
where reply_seq = #{replySeq}
