mapper.select:
select storage_name as storageName, storage_public as storagePublic
from tb_storage
where character_seq = #{characterSeq}

mapper.update:
update tb_content
set content_save = content_save + 1
where content_seq = #{contentSeq}

update tb_content
set content_save = content_save - 1
where content_seq = #{contentSeq}
