mapper.select:
select count(*)
from tb_user
where user_seq=#{user_seq}

select user_seq, user_id, user_email, user_birth
from tb_user
where user_seq=#{user_seq}

select count(*)
from tb_user
where user_id=#{user_id}

select count(*)
from tb_user
where user_id=#{userId} and user_pw=#{userPw}

select count(*)
from tb_user
where user_email=#{userEmail}
