mapper.select:
select count(*)
from tb_user
where user_id=#{userId}

select user_seq as userSeq, user_id as userId, user_email as userEmail, user_birth as userBirth, user_creatable_count as userCreatableCount
from tb_user
where user_id=#{userId}

select count(*)
from tb_user
where user_id=#{user_id}

select count(*)
from tb_user
where user_id=#{userId} and user_pw=#{userPw}

mapper.update:
update tb_user
set user_pw=#{userPw}
where user_id=#{userId}

update tb_user
set user_birth=#{userBirth}
where user_id=#{userId}

mapper.select:
select user_id
from tb_user
where user_email=#{userEmail}

select user_is_acitve
from tb_user
where user_email=#{userEmail}

select count(*)
from tb_user
where user_id=#{userId} and user_pw=#{userPw}

select user_seq
from tb_user
where user_id=#{userId}

mapper.update:
update tb_user
set email_is_auth=true
where user_id=#{userId}

mapper.select:
select user_email
from tb_user
where user_id=#{userId}

select user_creatable_count
from tb_user
where user_seq=#{userSeq}
